def resize_and_pad(image_path, size=(200, 200), bg_color=(255, 255, 255, 0)):
    try:
        img = Image.open(image_path).convert("RGBA")
        img.thumbnail(size, Image.LANCZOS)  # ar√°nyosan √°tm√©retezi (felnagy√≠t is!)

        background = Image.new("RGBA", size, bg_color)
        offset = ((size[0] - img.width) // 2, (size[1] - img.height) // 2)
        background.paste(img, offset)
        return background
    except Exception as e:
        st.warning(f"Nem siker√ºlt bet√∂lteni a k√©pet: {e}")
        return None


